#include <Servo.h>
Servo myservo;
int servoPin = 2;
int potPin = A0;
int value = 0;
int rot = 0;
void setup()
{
 pinMode(A0, INPUT);
 myservo.attach(2);
}
void loop()
{
 value=analogRead(A0);
 rot=map(value, 0, 1023, 0, 180);
 myservo.write(rot);
}

#include <Stepper.h>
const int stepsPerRevolution = 200;
Stepper myStepper(stepsPerRevolution, 8, 9, 10, 11);
void setup() {
 myStepper.setSpeed(0);
}
void loop() {
 int sensorReading = analogRead(A0);

 int motorSpeed = map(sensorReading, 0, 1040, 0, 150);

 myStepper.setSpeed(motorSpeed);

 myStepper.step(stepsPerRevolution / 100);

 delay(100);
}

int pirSensor = 12;
int ldrSensor = A0;
int relay = 2;
int ldrValue, pirValue;
unsigned long currentMillis;
void setup()
{
 Serial.begin(9600);
 pinMode(pirSensor, INPUT);
 pinMode(ldrSensor, INPUT);
 pinMode(relay, OUTPUT);
}
void loop()
{
 ldrValue = analogRead(ldrSensor);
 pirValue = digitalRead(pirSensor);
 ldrValue = map(ldrValue, 6, 679, 0, 1023);
 ldrValue = constrain(ldrValue, 0, 1023);
 Serial.println(ldrValue);

 if(ldrValue < 500){
 if(pirValue == LOW){
 digitalWrite(relay, LOW);
 } else{
 digitalWrite(relay,HIGH);
 delay(5000);
 }
 }
 else if(ldrSensor >= 500){
 if(pirValue == LOW){
 digitalWrite(relay, LOW);
 } else{
 digitalWrite(relay,LOW);
 }
 }
}

const int ledPin = 13;
const int buzzerPin = 8;
void setup() {
 pinMode(ledPin, OUTPUT);

 pinMode(buzzerPin, OUTPUT);
}
void loop() {
 digitalWrite(ledPin, HIGH);

 tone(buzzerPin, 1000);

 delay(1000);

 digitalWrite(ledPin, LOW);

 noTone(buzzerPin);

 delay(2000);
}

const int pirPin = 2;
const int ledPin = 13;
void setup() {
 pinMode(ledPin, OUTPUT);
 pinMode(pirPin, INPUT);
 Serial.begin(9600);
}
void loop() {
 int pirState = digitalRead(pirPin);
 if (pirState == HIGH) {
 digitalWrite(ledPin, HIGH);
 Serial.println("Motion detected!");
 } else {
 digitalWrite(ledPin, LOW);
 }
 delay(100);
}

#include "DHT.h"
#define DHTPIN 2
#define DHTTYPE DHT22
DHT dht(DHTPIN, DHTTYPE);
void setup() {
 Serial.begin(9600);
 Serial.println("DHT22 Sensor Reading...");
 dht.begin();
}
void loop() {
 float humidity = dht.readHumidity();
 float temperature = dht.readTemperature();
 if (isnan(humidity) || isnan(temperature)) {
 Serial.println("Failed to read from DHT22 sensor!");
 return;
 }
 Serial.print("Temperature: ");
 Serial.print(temperature);
 Serial.print(" °C | Humidity: ");
 Serial.print(humidity);
 Serial.println(" %");
 delay(2000);
}

float temp;
void setup()
{
Serial.begin(9600);
 pinMode(A1,INPUT);
 pinMode(4,OUTPUT);
 pinMode(3,OUTPUT);
}
void loop()
{
 temp=analogRead(A1);
 temp=((temp*5)/1024);
 temp=(temp-0.5)*100;
 Serial.print(temp);
 if(temp<=80)
 {
 Serial.println("Flame not detected");
 digitalWrite(4,LOW);
 digitalWrite(3,LOW);
 }
 else
 {
 Serial.println("Flame detected");
 digitalWrite(4,HIGH);
 digitalWrite(3,HIGH);
 }
 }

#include <LiquidCrystal.h>
int seconds = 0;
LiquidCrystal lcd(12, 11, 5, 4, 3, 2);
int triggerPin = 7;
int echoPin = 6;
long duration;
int distance;
void setup()
{
 lcd.begin(16, 2);
 lcd.print("Sensor Reading");

 pinMode(triggerPin, OUTPUT);
 pinMode(echoPin, INPUT);
}
void loop()
{
 digitalWrite(triggerPin, LOW);
 delayMicroseconds(2);
 digitalWrite(triggerPin, HIGH);
 delayMicroseconds(10);
 digitalWrite(triggerPin, LOW);

 duration = pulseIn(echoPin, HIGH);

 distance = duration * 0.0342 / 2;

 lcd.setCursor(0, 1);
 lcd.print("Distance: ");
 lcd.print(distance);
 lcd.print(" cm ");

 delay(200);
}
